import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const LandingContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const Content = styled.div`
  text-align: center;
  max-width: 600px;
  background: white;
  border-radius: 20px;
  padding: 60px 40px;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: #1e293b;
  margin-bottom: 16px;
  line-height: 1.2;
`;

const Subtitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #3b82f6;
  margin-bottom: 24px;
`;

const Description = styled.p`
  font-size: 1.125rem;
  color: #64748b;
  margin-bottom: 40px;
  line-height: 1.7;
`;

const FeaturesList = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
  margin-bottom: 40px;
  text-align: left;
`;

const Feature = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.875rem;
  color: #475569;
`;

const CheckIcon = styled.span`
  color: #10b981;
  font-weight: bold;
`;

const StartButton = styled.button`
  background: linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%);
  color: white;
  border: none;
  padding: 16px 32px;
  font-size: 1.125rem;
  font-weight: 600;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 14px 0 rgba(59, 130, 246, 0.4);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px 0 rgba(59, 130, 246, 0.6);
  }

  &:active {
    transform: translateY(0);
  }
`;

const StatsContainer = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 40px;
  padding-top: 40px;
  border-top: 1px solid #e2e8f0;
`;

const Stat = styled.div`
  text-align: center;
`;

const StatNumber = styled.div`
  font-size: 2rem;
  font-weight: 700;
  color: #3b82f6;
`;

const StatLabel = styled.div`
  font-size: 0.875rem;
  color: #64748b;
  margin-top: 4px;
`;

const LandingPage = () => {
  const navigate = useNavigate();

  const handleStart = () => {
    navigate('/quiz');
  };

  return (
    <LandingContainer>
      <Content>
        <Title>Free Profile Evaluation</Title>
        <Subtitle>Assess Your Tech Career Readiness</Subtitle>
        <Description>
          Get a comprehensive evaluation of your profile for tech roles. 
          Discover your strengths, identify gaps, and receive personalized 
          recommendations to accelerate your career growth.
        </Description>

        <FeaturesList>
          <Feature>
            <CheckIcon>✓</CheckIcon>
            Profile Strength Analysis
          </Feature>
          <Feature>
            <CheckIcon>✓</CheckIcon>
            Skill Gap Assessment
          </Feature>
          <Feature>
            <CheckIcon>✓</CheckIcon>
            Salary Readiness Check
          </Feature>
          <Feature>
            <CheckIcon>✓</CheckIcon>
            Interview Preparation Tips
          </Feature>
          <Feature>
            <CheckIcon>✓</CheckIcon>
            Personalized Resources
          </Feature>
          <Feature>
            <CheckIcon>✓</CheckIcon>
            Peer Comparison
          </Feature>
        </FeaturesList>

        <StartButton onClick={handleStart}>
          Start Free Evaluation
        </StartButton>

        <StatsContainer>
          <Stat>
            <StatNumber>5min</StatNumber>
            <StatLabel>Evaluation Time</StatLabel>
          </Stat>
          <Stat>
            <StatNumber>100%</StatNumber>
            <StatLabel>Free</StatLabel>
          </Stat>
          <Stat>
            <StatNumber>13</StatNumber>
            <StatLabel>Detailed Metrics</StatLabel>
          </Stat>
        </StatsContainer>
      </Content>
    </LandingContainer>
  );
};

export default LandingPage;
